select `inventari_hist`.`id` AS `id`,`inventari_hist`.`inventari_id` AS `inventari_id`,`inventari_hist`.`centre_id` AS `centre_id`,`inventari_hist`.`centre_exercici_id` AS `centre_exercici_id`,(ifnull(`inventari`.`import_compra`,0) - ifnull(`inventari`.`valor_residual`,0)) AS `import_compra`,sum(`inventari_hist_acumulat`.`import`) AS `import_acumulat`,`inventari_hist`.`import` AS `amortitzacio_import`,ifnull(`inventari_hist`.`amortitzacio_acum`,0) AS `amortitzacio_acumulat`,ifnull(`inventari_hist`.`valor_net`,0) AS `amortitzacio_valor_net`,(sum(`inventari_hist_acumulat`.`import`) - ifnull(`inventari_hist`.`amortitzacio_acum`,0)) AS `check_acumulat`,(((ifnull(`inventari`.`import_compra`,0) - ifnull(`inventari`.`valor_residual`,0)) - sum(ifnull(`inventari_hist_acumulat`.`import`,0))) - ifnull(`inventari_hist`.`valor_net`,0)) AS `check_valor_net` from ((`inventari_hist` left join `inventari` on((`inventari`.`id` = `inventari_hist`.`inventari_id`))) left join `inventari_hist` `inventari_hist_acumulat` on(((`inventari_hist_acumulat`.`id` = `inventari_hist`.`id`) and (`inventari_hist_acumulat`.`inventari_id` = `inventari_hist`.`inventari_id`)))) group by `inventari_hist`.`id`